class Promise{
  constructor(executor){
    this.state='pending';
    this.value=undefined;
    this.reason=undefined;
    let resolve=value=>{
      if(this.state=='pending'){
        this.state='fulfilled';
        this.value=value;
      }
    }
    let reject=reason=>{
      if(this.state=='pending'){
        this.state='rejected';
        this.reason=reason;
      }
    }
    // executor  reject
    try{
      executor(resolve,reject)
    }catch(err){
      reject(err)
    }
  }
}
```

